// individual coins
import axios from 'axios'
import { useParams } from 'react-router-dom'
import React, { useState, useEffect } from 'react'
import DOMPurify from 'dompurify';

const Coin = () => {
    const [coin, setCoin] = useState({});
    const params = useParams()
    const url = `https://api.coingecko.com/api/v3/coins/${params.coinId}`;
    useEffect(() => {
        axios.get(url)
        .then((response) => {
            setCoin(response.data);
        })
        .catch((err) => {
            console.log(err);
        })
    }, );

    return (
        <div>
        <div className="bg-gray-900 rounded-lg">
            <div className='p-5 flex justify-between items-center'>
                <div className="flex justify-between items-center">
                    {coin.image ? <img src={coin.image.small} alt='' className="mx-1 h-50" /> : null}
                    <h1 className='text-4xl mx-1'>{coin.name}</h1>
                </div>
                <span className='bg-purple-700 rounded-lg p-1'>Rank # {coin.market_cap_rank}</span>
            </div>
            <div className='px-10'>
                Current price: {coin.market_data?.current_price ? <h1>${coin.market_data.current_price.usd.toLocaleString()}</h1> : null}
            </div>  

            <div className='content'>
                <div className='stats'>
                    <div className='left'>
                        <div className='row'>
                            <h4>24 Hour Low</h4>
                            {coin.market_data?.low_24h ? <p>${coin.market_data.low_24h.usd.toLocaleString()}</p> : null}
                        </div>
                        <div className='row'>
                            <h4>24 Hour High</h4>
                            {coin.market_data?.high_24h ? <p>${coin.market_data.high_24h.usd.toLocaleString()}</p> : null}                            </div>
                    </div>
                    <div className='right'>
                        <div className='row'>
                            <h4>Market Cap</h4>
                            {coin.market_data?.market_cap ? <p>${coin.market_data.market_cap.usd.toLocaleString()}</p> : null}
                        </div>
                        <div className='row'>
                            <h4>Circulating Supply</h4>
                            {coin.market_data ? <p>{coin.market_data.circulating_supply}</p> : null}
                        </div>

                    </div>
                    <div className='content'>
                    <div className='about p-10'>
                        <h3 className="text-xl text-semibold">About</h3>
                        <p dangerouslySetInnerHTML={{
                            __html: DOMPurify.sanitize(coin.description ? coin.description.en : ''),
                        }}>

                        </p>

                    </div>
                     </div>
                </div>  
            </div>          
        </div>
        </div>
    )
}

export default Coin;
